mapper.select:
SELECT 
FROM t_order
WHERE user_id = #{filter.userId}

    AND status = #{filter.status}


    AND created_at >= #{filter.startDate}


    AND created_at <= #{filter.endDate}


    AND (
        order_no LIKE CONCAT('%', #{filter.keyword}, '%')
        OR sender_name LIKE CONCAT('%', #{filter.keyword}, '%')
        OR receiver_name LIKE CONCAT('%', #{filter.keyword}, '%')
    )

ORDER BY 


        ${filter.sortField} 

            ${filter.sortDirection}



        created_at DESC


LIMIT #{filter.offset}, #{filter.size}

SELECT COUNT(*)
FROM t_order
WHERE user_id = #{filter.userId}

    AND status = #{filter.status}


    AND created_at >= #{filter.startDate}


    AND created_at <= #{filter.endDate}


    AND (
        order_no LIKE CONCAT('%', #{filter.keyword}, '%')
        OR sender_name LIKE CONCAT('%', #{filter.keyword}, '%')
        OR receiver_name LIKE CONCAT('%', #{filter.keyword}, '%')
    )

SELECT 
FROM t_order
WHERE courier_id = #{filter.courierId}

    AND status = #{filter.status}


    AND created_at >= #{filter.startDate}


    AND created_at <= #{filter.endDate}


    AND (
        order_no LIKE CONCAT('%', #{filter.keyword}, '%')
        OR sender_name LIKE CONCAT('%', #{filter.keyword}, '%')
        OR receiver_name LIKE CONCAT('%', #{filter.keyword}, '%')
    )

ORDER BY 


        ${filter.sortField} 

            ${filter.sortDirection}



        created_at DESC


LIMIT #{filter.offset}, #{filter.size}

SELECT COUNT(*)
FROM t_order
WHERE courier_id = #{filter.courierId}

    AND status = #{filter.status}


    AND created_at >= #{filter.startDate}


    AND created_at <= #{filter.endDate}


    AND (
        order_no LIKE CONCAT('%', #{filter.keyword}, '%')
        OR sender_name LIKE CONCAT('%', #{filter.keyword}, '%')
        OR receiver_name LIKE CONCAT('%', #{filter.keyword}, '%')
    )

SELECT o.,
       u.username as user_username,
       c.name as courier_name
FROM t_order o
LEFT JOIN t_user u ON o.user_id = u.id
LEFT JOIN t_courier c ON o.courier_id = c.id


        AND o.status = #{filter.status}


        AND o.package_type = #{filter.packageType}


        AND o.created_at >= #{filter.startDate}


        AND o.created_at <= #{filter.endDate}


        AND (
            o.order_no LIKE CONCAT('%', #{filter.keyword}, '%')
            OR o.sender_name LIKE CONCAT('%', #{filter.keyword}, '%')
            OR o.sender_phone LIKE CONCAT('%', #{filter.keyword}, '%')
            OR o.receiver_name LIKE CONCAT('%', #{filter.keyword}, '%')
            OR o.receiver_phone LIKE CONCAT('%', #{filter.keyword}, '%')
            OR u.username LIKE CONCAT('%', #{filter.keyword}, '%')
            OR c.name LIKE CONCAT('%', #{filter.keyword}, '%')
        )


ORDER BY 


        o.${filter.sortField} 

            ${filter.sortDirection}



        o.created_at DESC


LIMIT #{filter.offset}, #{filter.size}

SELECT COUNT(*) FROM t_order o LEFT JOIN t_user u ON o.user_id = u.id LEFT JOIN t_courier c ON o.courier_id = c.id
AND o.status = #{filter.status}
AND o.package_type = #{filter.packageType}
AND o.created_at >= #{filter.startDate}
AND o.created_at <= #{filter.endDate}
AND ( o.order_no LIKE CONCAT('%', #{filter.keyword}, '%') OR o.sender_name LIKE CONCAT('%', #{filter.keyword}, '%') OR o.sender_phone LIKE CONCAT('%', #{filter.keyword}, '%') OR o.receiver_name LIKE CONCAT('%', #{filter.keyword}, '%') OR o.receiver_phone LIKE CONCAT('%', #{filter.keyword}, '%') OR u.username LIKE CONCAT('%', #{filter.keyword}, '%') OR c.name LIKE CONCAT('%', #{filter.keyword}, '%') )

SELECT o.,
       u.username as user_username,
       c.name as courier_name
FROM t_order o
LEFT JOIN t_user u ON o.user_id = u.id
LEFT JOIN t_courier c ON o.courier_id = c.id


        AND o.status = #{filter.status}


        AND o.created_at >= #{filter.startDate}


        AND o.created_at <= #{filter.endDate}


ORDER BY o.created_at DESC
